import React from 'react'
import {Button, Form, InputGroup } from 'react-bootstrap';



const HeroSection = () => {
  return (
    <>
        <div className='m-3 d-grid justify-content-center align-items-center' style={{
            backgroundColor: "black",
            height: '100vh'
        }}>
            <div>

            <h1 className='text-white'>Influencer Marketing Made Easy</h1>
            <h6 className='text-white mb-5'>Find and hire top Instagram, TikTok, YouTube, and UGC influencer to create unique content for your brand</h6>
            <InputGroup className="mb-3">
                <Form.Control
                placeholder="Search"
                aria-label="Search"
                aria-describedby="basic-addon2"
                />
                <Button variant="outline-secondary" id="button-addon2">
                    Search
                </Button>
            </InputGroup>
            </div>
        </div>
        <div className='my-5 py-3 text-white fw-' style={{
            backgroundColor: "#9A3B95"
        }}>
            <h1>Connecting Brands and Influencers Effortlessly.</h1>
        </div>
    </>
  )
}

export default HeroSection;
