import React, { useState } from "react";
import "./style.css";
import Swal from "sweetalert2";
import emailjs from "@emailjs/browser";
import { Button, Col, Form, Row } from "react-bootstrap";
export default function EmailVerify(props) {
  const [otpStatus, setotpstatus] = useState(false);
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    password: "",
    referralSource: "Choose...",
    checked: false,
  });
  const [errors, setErrors] = useState({});
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const fieldValue = type === "checkbox" ? checked : value;
    setFormData({
      ...formData,
      [name]: fieldValue,
    });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    // Validate the form
    const newErrors = {};
    if (!formData.fullName.trim()) {
      newErrors.fullName = "Full Name is required";
    }
    if (!formData.email.trim()) {
      newErrors.email = "Email is required";
    } else if (!isValidEmail(formData.email)) {
      newErrors.email = "Invalid email address";
    }
    if (!formData.password.trim()) {
      newErrors.password = "Password is required";
    }
    if (formData.referralSource === "Choose...") {
      newErrors.referralSource = "Please select an option";
    }
    if (!formData.checked) {
      newErrors.checked = "You must check the box";
    }
    setErrors(newErrors);
    // If there are no errors, submit the form
    if (Object.keys(newErrors).length === 0) {
      // You can access the form data here in the formData object
      console.log("Form data submitted:", formData);
      Swal.fire(
        "Thank You for Registration " + formData.fullName.trim(),
        "Check you email for verification link",
        "success"
      );
      const form = document.getElementById("myForm");
      emailjs
        .sendForm(
          "service_qo9nh4e", // Replace with your service ID
          "template_xh1ijhr", // Replace with your template ID
          formData,
          "lPidiJhuBIjxn13mLrXMo" // Replace with your user ID
        )
        .then(
          (result) => {
            console.log("Email sent successfully:", result.text);
          },
          (error) => {
            console.error("Error sending email:", error.text);
          }
        );

      setotpstatus(true);
    }
  };
  const isValidEmail = (email) => {
    // Basic email validation regex
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };
  return (
    <>
      <div>
        {otpStatus === false && (
          <div className="p-5 text-center bg-body-tertiary hero">
            <div className="container py-5">
              <h1 className="text-body-emphasis">
                {" "}
                Hello <strong className="gtext">@{props.userClaim} </strong>
                Create Your Page
              </h1>
              <Row className="justify-content-md-center mt-4">
                <Col xs lg="6" style={{ textAlign: "left" }}>
                  <Form onSubmit={handleSubmit} id="myForm">
                    <Form.Group className="mb-3" controlId="formGridAddress1">
                      <Form.Control
                        type="text"
                        name="fullName"
                        placeholder="Full Name"
                        value={formData.fullName}
                        onChange={handleChange}
                        isInvalid={!!errors.fullName}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.fullName}
                      </Form.Control.Feedback>
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="formGridAddress2">
                      <Form.Control
                        type="email"
                        name="email"
                        placeholder="Email"
                        value={formData.email}
                        onChange={handleChange}
                        isInvalid={!!errors.email}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.email}
                      </Form.Control.Feedback>
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="formGridAddress2">
                      <Form.Control
                        type="password"
                        name="password"
                        placeholder="Password"
                        value={formData.password}
                        onChange={handleChange}
                        isInvalid={!!errors.password}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.password}
                      </Form.Control.Feedback>
                    </Form.Group>

                    <Form.Group as={Col} controlId="formGridState">
                      <Form.Select
                        name="referralSource"
                        defaultValue={formData.referralSource}
                        onChange={handleChange}
                        isInvalid={!!errors.referralSource}
                      >
                        <option disabled>How did you hear about us?</option>
                        <option>Option 1</option>
                        <option>Option 2</option>
                        {/* Add more options */}
                      </Form.Select>
                      <Form.Control.Feedback type="invalid">
                        {errors.referralSource}
                      </Form.Control.Feedback>
                    </Form.Group>

                    <Form.Group className="mb-3 mt-3" id="formGridCheckbox">
                      <Form.Check
                        type="checkbox"
                        name="checked"
                        label="Check me out"
                        checked={formData.checked}
                        onChange={handleChange}
                        isInvalid={!!errors.checked}
                      />
                      <Form.Control.Feedback type="invalid">
                        {errors.checked}
                      </Form.Control.Feedback>
                    </Form.Group>
                    <Button
                      variant="primary"
                      className="button-42 w-100"
                      type="submit"
                    >
                      Sign Up
                    </Button>
                  </Form>

                  <p style={{ textAlign: "center", color: "#5d5d5d" }}>
                    By signing up, you agree to our Terms and Privacy Policy.
                  </p>
                </Col>
              </Row>
            </div>
          </div>
        )}
        {otpStatus && (
          <div class="p-5 text-center bg-body-tertiary hero">
            <div class="container py-5">
              <h1 class="text-body-emphasis">OTP</h1>
            </div>
          </div>
        )}
      </div>
    </>
  );
}
