import Alert from "react-popup-alert";
import React, { useState } from "react";
import "./style.css";
export default function MbAlert(props) {
  const [alert, setAlert] = useState({
    type: props.type, //success | warning | error
    text: props.message, //message
    show: true,
    header: props.header,
  });
  function onCloseAlert() {
    setAlert({
      header: "",
      type: "",
      text: "",
      show: false,
    });
  }
  return (
    <>
      <Alert
        header={alert.header}
        btnText={"Close"}
        text={alert.text}
        type={alert.type}
        show={alert.show}
        onClosePress={onCloseAlert}
        pressCloseOnOutsideClick={true}
        showBorderBottom={true}
        alertStyles={{}}
        headerStyles={{}}
        textStyles={{}}
        buttonStyles={{}}
      />
    </>
  );
}
