import React, { useState } from "react";
import "./style.css";
export default function EmailVerify(props) {
  const [otpStatus, setotpstatus] = useState(false);
  const [EmailVerify, setemailverify] = useState(null);
  return (
    <>
      <div>
        {otpStatus === false && (
          <div class="p-5 text-center bg-body-tertiary hero">
            <div class="container py-5">
              <h1 class="text-body-emphasis">
                {" "}
                Hello <strong className="gtext">
                  @{props.userClaim}{" "}
                </strong>{" "}
              </h1>
              <h2>Verify your email Email </h2>
              <input
                type="email"
                placeholder="exp..name@email.com"
                onChange={(e) => setemailverify(e)}
              />
              <button type="submit" className="btn btn-dark"></button>
            </div>
          </div>
        )}
        :
        {
          <div class="p-5 text-center bg-body-tertiary hero">
            <div class="container py-5">
              <h1 class="text-body-emphasis">OTP</h1>
            </div>
          </div>
        }
      </div>
    </>
  );
}
