import React, { useState } from "react";
import "./style.css";
import EmailVerify from "./EmailVerify";
export default function Signin() {
  const [claimtext, setclaimtext] = useState("");
  const [value, setvalue] = useState("");
  const [status, setstatus] = useState(false);
  const headlerclaim = () => {
    console.log(claimtext.target.value);
    setvalue(claimtext.target.value);
    setstatus(true);
  };
  return (
    <>
      {status === false && (
        <div>
          <div class="p-5 text-center bg-body-tertiary hero">
            <div class="container py-5">
              <h1 class="text-body-emphasis">Make Money as a Creator</h1>
              <p class="col-lg-8 mx-auto lead">
                The simple way to sell, manage, and get paid for your Instagram,
                TikTok, YouTube, and UGC brand deals
              </p>
              <div class="username-holder">
                <div class="username-domain">www.marbiz.com/</div>
                <div class="username-input-holder">
                  <input
                    type="text"
                    class="username-text"
                    onChange={(e) => setclaimtext(e)}
                    placeholder="yourname"
                  />
                </div>
                <div class="username-btn" onClick={headlerclaim}>
                  Claim
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
      {status && (
        <>
          <EmailVerify userClaim={value} />
        </>
      )}
    </>
  );
}
